mapper.select:
SELECT dept.id, dept.parent_id, dept.`name`, dept.abbr, dept.`status`, dept.corp_id, corp.`name` AS corpName FROM ktf_org_dept dept LEFT JOIN ktf_org_corp corp ON dept.corp_id = corp.id
dept.`status` = ${params.status}
AND dept.corp_id = ${params.corpId}
AND dept.name like '%${params.name}%'
ORDER BY dept.parent_id, dept.id

SELECT t.id, t.parent_id, t.`name`, t.abbr, t.`status` FROM ktf_org_dept t
FIND_IN_SET(t.id, org_dept_getChildren(#{id}))
AND t.id != #{id}
AND t.`status` = #{status}

SELECT t.id, t.parent_id, t.`name`, t.abbr, t.`status` FROM ktf_org_dept t
FIND_IN_SET(t.id, org_dept_getParents(#{id}))
AND t.id != #{id}
AND t.`status` = #{status}
