mapper.select:
        SELECT
	        t.id
        FROM
        	ktf_sys_resource t
        
            
                t.id=#{params.id}
            
            
                AND t.status=#{params.status}
            
            
                AND t.parent_id=#{params.parentId}
            
            
                AND t.resource_type=#{params.resourceType}
            
        
        order by t.seq asc

        SELECT
	        t.id,
	        t.parent_id,
	        t.name,
	        t.resource_type
        FROM
        	ktf_sys_resource t
        
            
                t.id=#{params.id}
            
            
                AND t.status=#{params.status}
            
            
                AND t.parent_id=#{params.parentId}
            
            
                AND t.resource_type IN 
                  
      				#{item}  
     			  
            
        
        order by t.seq asc

SELECT r.id, r.parent_id, r.`name`, r.description, r.url, r.icon, r.seq, r.resource_type, r.status, r.hidden, if ((SELECT count(1) FROM ktf_sys_resource r1 WHERE r1.parent_id= r.id) >0, true, false) as has_children, if ((SELECT count(1) FROM ktf_sys_resource r1 WHERE r1.parent_id= r.id) >0, false, true) as is_leaf FROM ktf_sys_resource r
r.resource_type IN
#{item}
AND r.parent_id = #{params.parentId}
AND r.`name` like '%#{params.name}%'
AND r.`status` = #{params.status}
AND r.hidden = #{params.hidden}
ORDER BY r.parent_id, r.seq

SELECT r.id, r.parent_id, r.`name`, r.url, r.icon FROM ktf_sys_resource r
r.id IN ( SELECT rr.resource_id FROM ktf_sys_user_role ur LEFT JOIN ktf_sys_role_resource rr ON ur.role_id = rr.role_id WHERE ur.user_id = #{params.userId} )
AND r.resource_type IN
#{item}
AND r.`status` = #{params.status}
AND r.hidden = #{params.hidden}
ORDER BY r.parent_id, r.seq

SELECT DISTINCT r.url FROM ktf_sys_resource r
r.url is not null
AND r.resource_type = #{params.resourcetype}
AND r.id IN ( SELECT rr.resource_id FROM ktf_sys_role_resource rr WHERE rr.role_id IN
#{item}
)
AND r.`status` = #{params.status}
AND r.hidden = #{params.hidden}

SELECT r.id, r.parent_id, r.`name`, r.description, r.url, r.icon, r.seq, r.resource_type, r.status, r.hidden, if ((SELECT count(1) FROM ktf_sys_resource r1 WHERE r1.parent_id= r.id) >0, true, false) as has_children, if ((SELECT count(1) FROM ktf_sys_resource r1 WHERE r1.parent_id= r.id) >0, false, true) as is_leaf FROM ktf_sys_resource r
FIND_IN_SET(r.id, sys_resource_getChildren(#{id}))
r.parent_id = #{id}
AND r.id != #{id}
AND r.`status` = #{status}
AND r.hidden = #{hidden}
ORDER BY r.seq

    SELECT
		r.id,
		r.parent_id,
		r.`name`,
		r.url,
		r.icon
	FROM
		ktf_sys_resource r
	
	    FIND_IN_SET(r.id, sys_resource_getParents(#{id}))
	    
	    	AND  r.resource_type  = #{resourceType}
	    
	    
	    	AND r.id != #{id}
	    
		
			AND r.`status` = #{status}
		
		
			AND r.hidden = #{hidden}
