mapper.select:
SELECT id, pid, `code`, pcode, `name`, type, `status`, IF ((SELECT count(1) FROM ktf_sys_region t1 WHERE t1.pid = t.id) >0, TRUE, FALSE) as has_children, IF ((SELECT count(1) FROM ktf_sys_region t1 WHERE t1.pid = t.id) > 0, FALSE, TRUE ) AS is_leaf FROM ktf_sys_region t
t.pid = ${params.pid}
AND t.`status` = ${params.status}
AND (t.name LIKE '%${params.keyword}%' OR t.code LIKE '%${params.keyword}%')
ORDER BY t.id

SELECT id, pid, `code`, pcode, `name`, type, `status`, IF ((SELECT count(1) FROM ktf_sys_region t1 WHERE t1.pid = t.id) >0, TRUE, FALSE) as has_children, IF ((SELECT count(1) FROM ktf_sys_region t1 WHERE t1.pid = t.id) > 0, FALSE, TRUE ) AS is_leaf FROM ktf_sys_region t
t.pid = #{pid}
t.pid LIKE '%${pid}%'
AND t.`status` = #{status}
ORDER BY t.id
