mapper.select:
SELECT t.id, t.parent_id, t.var_code, t.var_name, t.var_value, t.type, t.`status`, if ((SELECT count(1) FROM ktf_sys_dic t1 WHERE t1.parent_id= t.id) >0, true, false) as has_children, if ((SELECT count(1) FROM ktf_sys_dic t1 WHERE t1.parent_id= t.id) >0, false, true) as is_leaf FROM ktf_sys_dic t
t.parent_id = ${params.parentId}
AND t.`status` = ${params.status}
AND (t.var_name LIKE '%${params.keyword}%' OR t.var_code LIKE '%${params.keyword}%')
ORDER BY t.id

SELECT t.id, t.parent_id, t.var_code, t.var_name, t.var_value, t.type, t.`status`, if ((SELECT count(1) FROM ktf_sys_dic t1 WHERE t1.parent_id= t.id) >0, true, false) as has_children, if ((SELECT count(1) FROM ktf_sys_dic t1 WHERE t1.parent_id= t.id) >0, false, true) as is_leaf FROM ktf_sys_dic t
t.parent_id = #{id}
FIND_IN_SET(t.id, sys_dic_getChildren(#{id}))
AND FIND_IN_SET(t.id, sys_dic_getChildren((SELECT id FROM ktf_sys_dic WHERE var_code=#{varCode})))
AND t.id != #{id}
AND t.var_code != #{varCode}
AND t.`status` = #{status}
ORDER BY t.id

SELECT t.id, t.var_name, if ((SELECT count(1) FROM ktf_sys_dic t1 WHERE t1.parent_id= t.id) >0, false, true) as is_leaf FROM ktf_sys_dic t
t.parent_id = ${params.parentId}
AND t.`status` = ${params.status}
ORDER BY t.id

SELECT t.id, t.parent_id, t.var_code, t.var_name, t.var_value, t.type, t.`status`, if ((SELECT count(1) FROM ktf_sys_dic t1 WHERE t1.parent_id= t.id) >0, true, false) as has_children, if ((SELECT count(1) FROM ktf_sys_dic t1 WHERE t1.parent_id= t.id) >0, false, true) as is_leaf FROM ktf_sys_dic t
FIND_IN_SET(t.id, sys_dic_getParents(#{id}))
AND t.parent_id = #{parentId}
AND t.id != #{id}
AND t.`status` = #{status}

SELECT A.var_code AS `key`, t3.var_value AS `value` FROM ktf_sys_dic t3 RIGHT JOIN ( SELECT t2.id, t2.var_code FROM ktf_sys_dic t2 WHERE t2.parent_id IN ( SELECT t1.id FROM ktf_sys_dic t1
t1.var_code IN
#{item}
) ) A ON t3.parent_id = A.id
t3.var_code = #{varCode}
